  async sendChatMessage(userId, message) {
    const user = await this.getUser(userId);
    if (!user || user.banned) {
      throw new Error('Cannot send message');
    }

    // Check if user is muted
    if (user.muted && user.muteExpires && Date.now() < user.muteExpires) {
      const timeLeft = Math.ceil((user.muteExpires - Date.now()) / 60000);
      throw new Error(`You are muted for ${timeLeft} more minutes`);
    }

    // Remove mute if expired
    if (user.muted && user.muteExpires && Date.now() >= user.muteExpires) {
      await this.updateUser(userId, {
        muted: false,
        muteExpires: null
      });
    }

    const messages = await this.getChatMessages();
    const newMessage = {
      id: randomUUID(),
      userId,
      nickname: user.nickname,
      message: message.trim(),
      timestamp: Date.now(),
      deleted: false,
      adminLevel: user.admin || 0  // Store admin level at time of message
    };

    messages.push(newMessage);

    // Keep only last 50 messages
    if (messages.length > 50) {
      messages.splice(0, messages.length - 50);
    }

    await this.saveChatMessages(messages);
    return newMessage;
  }

  async deleteChatMessage(messageId, adminUserId) {
    const admin = await this.getUser(adminUserId);
    if (!admin || admin.admin < 1) {
      throw new Error('Admin access required');
    }

    const messages = await this.getChatMessages();
    const messageIndex = messages.findIndex(msg => msg.id === messageId);

    if (messageIndex === -1) {
      throw new Error('Message not found');
    }

    messages[messageIndex].deleted = true;
    messages[messageIndex].deletedBy = admin.nickname;
    messages[messageIndex].deletedAt = Date.now();

    await this.saveChatMessages(messages);
    return messages[messageIndex];
  }

  // Reports System
  async createReport(userId, subject, category, initialMessage) {
    const user = await this.getUser(userId);
    if (!user || user.banned) {
      throw new Error('Cannot create report');
    }

    // Check if user has active report
    const existingReports = await this.getUserReports(userId);
    const hasActiveReport = existingReports.some(report => report.status === 'open');
    if (hasActiveReport) {
      throw new Error('You already have an active report');
    }

    const reportId = randomUUID();
    const report = {
      id: reportId,
      userId,
      userNickname: user.nickname,
      subject,
      category,
      status: 'open',
      createdAt: Date.now(),
      closedAt: null,
      closedBy: null,
      isMarked: false,
      hasNewMessages: false
    };

    let reports = [];
    try {
      const data = await fs.readFile('./data/reports.json', 'utf8');
      reports = JSON.parse(data);
    } catch (error) {
      // File doesn't exist, start with empty array
    }

    reports.push(report);
    await fs.writeFile('./data/reports.json', JSON.stringify(reports, null, 2));

    // Create initial message
    if (initialMessage) {
      await this.createReportMessage(reportId, userId, initialMessage, false);
    }

    return report;
  }

  async getUserReports(userId) {
    try {
      const data = await fs.readFile('./data/reports.json', 'utf8');
      const reports = JSON.parse(data);
      return reports.filter(report => report.userId === userId);
    } catch (error) {
      return [];
    }
  }

  async getAdminReports() {
