import React, { useState, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Badge } from '@/components/ui/badge';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { useToast } from '@/hooks/use-toast';
import { useLanguage } from '@/contexts/language-context';
import { Settings, Crown, Users, Shield } from 'lucide-react';

const apiRequest = async (url, options = {}) => {
  const response = await fetch(url, {
    credentials: 'include',
    ...options,
    headers: {
      'Content-Type': 'application/json',
      ...options.headers,
    },
  });

  if (!response.ok) {
    const error = await response.json().catch(() => ({ message: 'Request failed' }));
    throw new Error(error.message || 'Request failed');
  }

  return response.json();
};

export const AdminPanel = ({ user, isOpen: externalIsOpen, onOpenChange }) => {
  const { t } = useLanguage();
  const { toast } = useToast();
  const [internalIsOpen, setInternalIsOpen] = useState(false);
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [selectedUser, setSelectedUser] = useState('');
  const [action, setAction] = useState('');
  const [amount, setAmount] = useState('');

  // Use external control if provided, otherwise use internal state
  const isOpen = externalIsOpen !== undefined ? externalIsOpen : internalIsOpen;
  const setIsOpen = onOpenChange || setInternalIsOpen;

  const isSuperAdmin = user.nickname === 'Ca6aka';

  const fetchUsers = async () => {
    try {
      setLoading(true);
      const data = await apiRequest('/api/admin/users');
      setUsers(data.users);
    } catch (error) {
      toast({
        title: t('error'),
        description: error.message,
        variant: 'destructive'
      });
    } finally {
      setLoading(false);
    }
  };

  const handleAdminAction = async () => {
    if (!selectedUser || !action) {
      toast({
        title: t('error'),
        description: t('pleaseSelectUserAndAction'),
        variant: 'destructive'
      });
      return;
    }

    // Check if amount is required for money actions
    if ((action === 'addMoney' || action === 'removeMoney') && (!amount || amount <= 0)) {
      toast({
        title: t('error'),
        description: t('pleaseEnterValidAmount'),
        variant: 'destructive'
      });
      return;
    }

    try {
      setLoading(true);
      await apiRequest('/api/admin/manage', {
        method: 'POST',
        body: JSON.stringify({
          userId: selectedUser,
          action: action,
          amount: action === 'addMoney' || action === 'removeMoney' ? parseInt(amount) : undefined
        })
      });

      toast({
        title: t('success'),
        description: t('actionSuccessfullyCompleted').replace('{action}', action)
      });

      fetchUsers();
      setSelectedUser('');
      setAction('');
      setAmount('');
    } catch (error) {
      toast({
        title: t('error'),
        description: error.message,
        variant: 'destructive'
      });
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (isOpen) {
      fetchUsers();
    }
  }, [isOpen]);

  if (user.admin < 1) return null;

  return (
    <Dialog open={isOpen} onOpenChange={setIsOpen}>
      {externalIsOpen === undefined && (
        <DialogTrigger asChild>
          <Button
            variant="outline"
            size="sm"
            className="bg-purple-600/20 hover:bg-purple-600/30 border-purple-500/50 text-purple-200"
            data-testid="button-admin-panel"
          >
            {isSuperAdmin ? (
              <>
                <Crown className="w-4 h-4 mr-1" />
                {t('superAdminPanel')}
              </>
            ) : (
              <>
                <Shield className="w-4 h-4 mr-1" />
                {t('adminPanel')}
              </>
            )}
          </Button>
        </DialogTrigger>
      )}
      <DialogContent className="max-w-4xl bg-slate-900 text-slate-100 max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="flex items-center space-x-2">
            {isSuperAdmin ? <Crown className="w-5 h-5 text-yellow-400" /> : <Shield className="w-5 h-5 text-purple-400" />}
            <span>{isSuperAdmin ? t('superAdminPanel') : t('adminPanel')}</span>
          </DialogTitle>
        </DialogHeader>

        <div className="space-y-6">
          {/* Admin Stats */}
          <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
            <Card className="bg-slate-800/50 border-slate-700">
              <CardHeader className="pb-2">
                <CardTitle className="text-sm text-slate-400">{t('totalUsers')}</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold text-slate-100">
                  {users.length}
                </div>
              </CardContent>
            </Card>
            <Card className="bg-slate-800/50 border-slate-700">
              <CardHeader className="pb-2">
                <CardTitle className="text-sm text-slate-400">{t('onlineUsers')}</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold text-green-400">
                  {users.filter(u => u.isOnline).length}
                </div>
              </CardContent>
            </Card>
            <Card className="bg-slate-800/50 border-slate-700">
              <CardHeader className="pb-2">
                <CardTitle className="text-sm text-slate-400">{t('totalAdmins')}</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold text-purple-400">
                  {users.filter(u => u.admin > 0).length}
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Admin Controls */}
          {(user.admin >= 1) && (
            <Card className="bg-slate-800/50 border-slate-700">
              <CardHeader>
                <CardTitle className="flex items-center space-x-2">
                  <Settings className="w-5 h-5" />
                  <span>{isSuperAdmin ? t('adminManagement') : t('playerManagement')}</span>
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
                  <div className="space-y-2">
                    <Label>{t('selectUser')}</Label>
                    <Select value={selectedUser} onValueChange={setSelectedUser}>
                      <SelectTrigger>
                        <SelectValue placeholder={t('chooseUser')} />
                      </SelectTrigger>
                      <SelectContent>
                        {users.map(user => (
                          <SelectItem key={user.id} value={user.id}>
                            {user.nickname} 
                            {user.admin > 0 && <Badge className="ml-2 text-xs">Admin</Badge>}
                            <span className={`ml-2 w-2 h-2 rounded-full ${user.isOnline ? 'bg-green-400' : 'bg-red-400'}`} />
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>
                  <div className="space-y-2">
                    <Label>{t('action')}</Label>
                    <Select value={action} onValueChange={setAction}>
                      <SelectTrigger>
                        <SelectValue placeholder={t('chooseAction')} />
                      </SelectTrigger>
                      <SelectContent>
                        {(() => {
                          const selectedUserData = users.find(u => u.id === selectedUser);
                          const isSelf = selectedUserData?.nickname === user.nickname;

                          return (
                            <>
                              <SelectItem value="giveAdmin" disabled={isSelf || !isSuperAdmin}>
                                {t('giveAdmin')}
                              </SelectItem>
                              <SelectItem value="removeAdmin" disabled={isSelf || !isSuperAdmin}>
                                {t('removeAdmin')}
                              </SelectItem>
                              <SelectItem value="banUser" disabled={isSelf}>
                                {t('banUser')}
                              </SelectItem>
                              <SelectItem value="unbanUser" disabled={isSelf}>
                                {t('unbanUser')}
                              </SelectItem>
                              <SelectItem value="addMoney" disabled={!isSuperAdmin}>
                                {t('giveMoney')}
                              </SelectItem>
                              <SelectItem value="removeMoney" disabled={!isSuperAdmin}>
                                {t('takeMoney')}
                              </SelectItem>
                            </>
                          );
                        })()}
                      </SelectContent>
                    </Select>
                  </div>
                </div>
                {(action === 'addMoney' || action === 'removeMoney') && (
                  <div className="space-y-2">
                    <Label>{t('amount')}</Label>
                    <Input
                      type="number"
                      placeholder={t('enterAmount')}
                      value={amount}
                      onChange={(e) => setAmount(e.target.value)}
                      min="1"
                      data-testid="input-amount"
                    />
                  </div>
                )}
                <Button 
                  onClick={handleAdminAction}
                  disabled={loading || !selectedUser || !action}
                  className="w-full bg-purple-600 hover:bg-purple-700"
                >
                  {loading ? t('loading') : t('executeAction')}
                </Button>
              </CardContent>
            </Card>
          )}

          {/* User List */}
          <Card className="bg-slate-800/50 border-slate-700">
            <CardHeader>
              <CardTitle className="flex items-center space-x-2">
                <Users className="w-5 h-5" />
                <span>{t('userList')}</span>
              </CardTitle>
            </CardHeader>
            <CardContent>
              {loading ? (
                <div className="text-center py-4">{t('loading')}</div>
              ) : (
                <div className="max-h-96 overflow-y-auto space-y-2">
                  {users.map(user => (
                    <div key={user.id} className="flex items-center justify-between p-3 bg-slate-700/30 rounded-lg">
                      <div className="flex items-center space-x-3">
                        <div className={`w-3 h-3 rounded-full ${user.isOnline ? 'bg-green-400' : 'bg-red-400'}`} />
                        <span className="font-medium">{user.nickname}</span>
                        {user.admin > 0 && (
                          <Badge variant="secondary" className="text-xs">
                            {user.nickname === 'Ca6aka' ? 'Super Admin' : 'Admin'}
                          </Badge>
                        )}
                      </div>
                      <div className="text-sm text-slate-400">
                        {t('balance')}: ${user.balance?.toLocaleString() || 0}
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </CardContent>
          </Card>
        </div>
      </DialogContent>
    </Dialog>
  );
};

client/src/components/hosting-tab.jsx.content:
import { Button } from '@/components/ui/button';
import { useGame } from '@/contexts/game-context';
import { useLanguage } from '@/contexts/language-context';
import { useToast } from '@/hooks/use-toast';
import { SERVER_PRODUCTS, formatCurrency } from '@/lib/constants';
import { Lock } from 'lucide-react';

// Helper function to get server tier colors
const getServerTierColors = (serverId) => {
  // Weak servers (green/blue)
  if (serverId === 'basic-web') return { bg: 'from-green-500/20 to-green-600/20', icon: 'text-green-400', border: 'border-green-500/50' };
  if (serverId === 'database-server') return { bg: 'from-blue-500/20 to-blue-600/20', icon: 'text-blue-400', border: 'border-blue-500/50' };

  // Medium servers (yellow/orange)
  if (serverId === 'high-performance') return { bg: 'from-yellow-500/20 to-yellow-600/20', icon: 'text-yellow-400', border: 'border-yellow-500/50' };
  if (serverId === 'cdn-server') return { bg: 'from-orange-500/20 to-orange-600/20', icon: 'text-orange-400', border: 'border-orange-500/50' };

  // Powerful servers (red/light blue)
  if (serverId === 'gpu-server') return { bg: 'from-red-500/20 to-red-600/20', icon: 'text-red-400', border: 'border-red-500/50' };
  if (serverId === 'tpu-server') return { bg: 'from-cyan-500/20 to-cyan-600/20', icon: 'text-cyan-400', border: 'border-cyan-500/50' };

  // Default fallback
  return { bg: 'from-slate-500/20 to-slate-600/20', icon: 'text-primary', border: 'border-primary/50' };
};

export function HostingTab({ onTabChange }) {
  const { gameState, purchaseServer } = useGame();
  const { t } = useLanguage();
  const { toast } = useToast();

  const handlePurchaseServer = async (productId) => {
    const product = SERVER_PRODUCTS.find(p => p.id === productId);
    const currentServers = gameState.servers?.length || 0;
    const serverLimit = gameState.user?.serverLimit || 3;

    if (currentServers >= serverLimit) {
      toast({
        title: t('serverLimitReached'),
        description: t('serverLimitReachedDesc').replace('{serverLimit}', serverLimit),
        variant: "destructive",
      });
      return;
    }

    if (gameState.user.balance < product.price) {
      toast({
        title: t('insufficientFunds'),
        description: t('needMoreMoney').replace('{amount}', formatCurrency(product.price)),
        variant: "destructive",
      });
      return;
    }

    try {
      await purchaseServer(productId);
      toast({
        title: t('serverPurchased'),
        description: t('serverAddedToYourServers').replace('{serverName}', product.name),
      });
    } catch (error) {
      toast({
        title: t('purchaseFailed'),
        description: error.message,
        variant: "destructive",
      });
    }
  };

  if (!gameState.user) return null;

  const currentServers = gameState.servers?.length || 0;
  const serverLimit = gameState.user.serverLimit || 3;
  const canPurchase = currentServers < serverLimit;

  return (
    <div className="p-3 sm:p-6 max-w-6xl mx-auto">
      <div className="flex flex-col sm:flex-row items-start sm:items-center justify-between mb-4 sm:mb-6">
        <h2 className="text-xl sm:text-2xl font-bold text-slate-100 mb-2 sm:mb-0">{t('serverStore')}</h2>
        <div className="flex items-center space-x-2 sm:space-x-3">
          <span className="text-xs sm:text-sm text-slate-400">{t('availableSlots')}:</span>
          <span className="bg-slate-700 px-2 sm:px-3 py-1 rounded-lg text-xs sm:text-sm font-medium">
            {serverLimit - currentServers}/{serverLimit}
          </span>
        </div>
      </div>

      {/* Server Limit Warning */}
      {!canPurchase && (
        <div className="bg-red-500/10 border border-red-500/30 rounded-lg p-4 mb-6">
          <div className="flex items-center space-x-2">
            <i className="fas fa-lock text-red-400"></i>
            <span className="text-red-400 font-medium">{t('serverLimitReached')}</span>
          </div>
          <p className="text-sm text-slate-300 mt-1">
            {t('reachedServerLimit').replace('{serverLimit}', serverLimit)}
          </p>
        </div>
      )}

      {/* Server Products Grid */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-4 sm:gap-6">
        {SERVER_PRODUCTS.map((product) => {
          const userLevel = gameState.user.level || 1;
          const canAfford = gameState.user.balance >= product.price;
          const hasLevelRequirement = userLevel >= product.requiredLevel;
          const completedLearning = gameState.user.completedLearning || [];
          const hasLearningRequirement = !product.requiredLearning || completedLearning.includes(product.requiredLearning);
          const isDisabled = !canPurchase || !canAfford || !hasLevelRequirement || !hasLearningRequirement;

          return (
            <div key={product.id} className={`relative overflow-hidden bg-slate-800/50 backdrop-blur-sm border border-slate-700 rounded-xl p-4 sm:p-6 hover:${getServerTierColors(product.id).border} transition-all duration-300 transform hover:scale-105 ${!hasLevelRequirement || !hasLearningRequirement ? 'opacity-60' : ''}`}>
              {/* Gradient Background */}
              <div className={`absolute inset-0 bg-gradient-to-br ${getServerTierColors(product.id).bg} opacity-0 hover:opacity-100 transition-opacity duration-300 rounded-xl`}></div>
              <div className="flex flex-col sm:flex-row items-start sm:items-center space-y-2 sm:space-y-0 sm:space-x-4 mb-4">
                <div className="flex items-center space-x-3 flex-1">
                  <div className={`relative z-10 w-10 sm:w-12 h-10 sm:h-12 bg-gradient-to-br ${getServerTierColors(product.id).bg} rounded-lg flex items-center justify-center border ${getServerTierColors(product.id).border}`}>
                    {!hasLevelRequirement || !hasLearningRequirement ? (
                      <Lock className="text-slate-400 text-sm sm:text-lg" />
                    ) : (
                      <i className={product.icon + ` ${getServerTierColors(product.id).icon} text-sm sm:text-lg`}></i>
                    )}
                  </div>
                  <div className="relative z-10 flex-1">
                    <h3 className="font-semibold text-slate-100 text-sm sm:text-base">{product.name}</h3>
                    <p className="text-xs sm:text-sm text-slate-400">{product.type}</p>
                    {!hasLevelRequirement && (
                      <p className="text-xs text-red-400 mt-1">{t('requiresLevel').replace('{level}', product.requiredLevel)}</p>
                    )}
                    {!hasLearningRequirement && (
                      <p className="text-xs text-red-400 mt-1">{t('needLearningCourse')}</p>
                    )}
                  </div>
                </div>
                <div className="relative z-10 text-right">
                  <p className="text-base sm:text-lg font-bold text-primary">{formatCurrency(product.price)}</p>
                </div>
              </div>

              <div className="relative z-10 space-y-3 mb-6">
                <div className="flex justify-between items-center p-3 bg-slate-700/30 rounded-lg">
                  <span className="text-sm text-slate-400">{t('income')}</span>
                  <span className="font-semibold text-secondary">+{formatCurrency(product.incomePerMinute)}</span>
                </div>
                <div className="flex justify-between items-center p-3 bg-slate-700/30 rounded-lg">
                  <span className="text-sm text-slate-400">{t('monthlyCost')}</span>
                  <span className="font-semibold text-red-400">-{formatCurrency(product.monthlyCost)}</span>
                </div>
                <div className="flex justify-between items-center p-3 bg-slate-700/30 rounded-lg">
                  <span className="text-sm text-slate-400">{t('netProfitMonth')}</span>
                  <span className="font-semibold text-secondary">
                    +{formatCurrency((product.incomePerMinute * 60 * 24 * 30) - product.monthlyCost)}
                  </span>
                </div>
              </div>

              <Button 
                className="relative z-10 w-full"
                disabled={isDisabled}
                onClick={() => handlePurchaseServer(product.id)}
                variant={isDisabled ? "secondary" : "default"}
              >
                {!hasLevelRequirement ? t('requiresLevel').replace('{level}', product.requiredLevel) :
                 !hasLearningRequirement ? t('needLearningCourse') :
                 !canPurchase ? t('serverLimitReached') : 
                 !canAfford ? t('insufficientFunds') : 
                 t('purchaseServer')}
              </Button>

              {!canAfford && canPurchase && (
                <p className="relative z-10 text-xs text-red-400 mt-2 text-center">
                  {t('needMoreFunds').replace('{amount}', formatCurrency(product.price - gameState.user.balance))}
                </p>
              )}
            </div>
          );
        })}
      </div>

      {/* Upgrade Notice */}
      <div className="mt-8 bg-gradient-to-r from-purple-500/10 to-blue-500/10 border border-purple-500/30 rounded-xl p-6">
        <div className="flex items-center space-x-3 mb-3">
          <div className="w-10 h-10 bg-purple-500/20 rounded-lg flex items-center justify-center">
            <i className="fas fa-graduation-cap text-purple-400"></i>
          </div>
          <div>
            <h3 className="text-lg font-semibold text-purple-400">{t('wantMoreServers')}</h3>
            <p className="text-sm text-slate-400">{t('completeLearningCoursesToIncrease')}</p>
          </div>
        </div>
        <p className="text-sm text-slate-300 mb-4">
          {t('learningCoursesBenefit')}
        </p>
        <Button 
          variant="outline" 
          className="border-purple-500/30 text-purple-400 hover:bg-purple-500/10"
          onClick={() => onTabChange && onTabChange('learning')}
        >
          {t('browseLearningCourses')}
        </Button>
      </div>
    </div>
  );
}

client/src/components/learning-tab.jsx.content:
import { Button } from '@/components/ui/button';
import { Progress } from '@/components/ui/progress';
import { useGame } from '@/contexts/game-context';
import { useLanguage } from '@/contexts/language-context';
import { useToast } from '@/hooks/use-toast';
import { LEARNING_COURSES, formatCurrency, formatTime } from '@/lib/constants';
import { Lock } from 'lucide-react';
import { useQueryClient } from '@tanstack/react-query';

// Helper function to get learning course colors based on difficulty
const getLearningColors = (difficulty) => {
  if (difficulty === 'Beginner') return { bg: 'from-blue-500/20 to-blue-600/20', icon: 'text-blue-400', border: 'border-blue-500/50' };
  if (difficulty === 'Intermediate') return { bg: 'from-orange-500/20 to-orange-600/20', icon: 'text-orange-400', border: 'border-orange-500/50' };
  if (difficulty === 'Advanced') return { bg: 'from-red-500/20 to-red-600/20', icon: 'text-red-400', border: 'border-red-500/50' };
  if (difficulty === 'Expert') return { bg: 'from-purple-500/20 to-purple-600/20', icon: 'text-purple-400', border: 'border-purple-500/50' };
  if (difficulty === 'Master') return { bg: 'from-cyan-500/20 to-cyan-600/20', icon: 'text-cyan-400', border: 'border-cyan-500/50' };

  // Default fallback
  return { bg: 'from-blue-500/20 to-blue-600/20', icon: 'text-blue-400', border: 'border-blue-500/50' };
};

// Helper function to format reward text
const getRewardText = (reward, t) => {
  if (!reward || typeof reward !== 'object') return t('unknownReward');

  if (reward.type === 'serverSlots') {
    const amount = reward.amount ?? 0;
    return t('serverSlotReward')
      .replace('{amount}', amount)
      .replace('{plural}', amount > 1 ? 's' : '');
  } else if (reward.type === 'efficiency') {
    const amount = reward.amount ?? 0;
    return t('serverEfficiencyReward').replace('{amount}', amount);
  } else if (reward.type === 'serverUnlock') {
    const serverType = reward.serverType;
    const serverTypeName = serverType === 'gpu-server' ? t('gpuServer') : 
                           serverType === 'tpu-server' ? t('tpuServer') : 
                           t('specialServer');
    return t('unlockServerReward').replace('{serverType}', serverTypeName);
  }
  return t('unknownReward');
};

export function LearningTab() {
  const { gameState, startLearning } = useGame();
  const { t } = useLanguage();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const handleStartCourse = async (courseId) => {
    const course = LEARNING_COURSES.find(c => c.id === courseId);

    if (gameState.currentLearning) {
      toast({
        title: t('learningInProgressError'),
        description: t('oneCourseAtTime'),
        variant: "destructive",
      });
      return;
    }

    if (gameState.user.balance < course.price) {
      toast({
        title: t('insufficientFunds'),
        description: t('needMoneyForCourse').replace('{amount}', formatCurrency(course.price)),
        variant: "destructive",
      });
      return;
    }

    try {
      await startLearning(courseId);
      // Immediately update balance and user data
      queryClient.invalidateQueries({ queryKey: ['/api/auth/me'] });
      queryClient.invalidateQueries({ queryKey: ['/api/learning/current'] });
      toast({
        title: t('courseStarted'),
        description: t('courseStartedDesc').replace('{courseTitle}', course.title),
      });
    } catch (error) {
      toast({
        title: t('failedToStartCourse'),
        description: error.message,
        variant: "destructive",
      });
    }
  };

  if (!gameState.user) return null;

  return (
    <div className="p-6 max-w-6xl mx-auto">
      <div className="flex items-center justify-between mb-6">
        <h2 className="text-2xl font-bold text-slate-100">{t('learningCenter')}</h2>
        <div className="flex items-center space-x-3">
          <span className="text-sm text-slate-400">{t('currentBalance')}:</span>
          <span className="bg-slate-700 px-3 py-1 rounded-lg text-sm font-medium">
            {formatCurrency(gameState.user.balance)}
          </span>
        </div>
      </div>

      {/* Current Learning Progress */}
      {gameState.currentLearning && gameState.currentLearning.progress < 100 && (
        <div className="mb-8 bg-gradient-to-r from-purple-500/10 to-pink-500/10 border border-purple-500/30 rounded-xl p-6">
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-lg font-semibold text-purple-400">{t('currentCourse')}</h3>
            <span className="bg-purple-500/20 text-purple-300 px-3 py-1 rounded-lg text-sm font-medium">
              {gameState.currentLearning.difficulty}
            </span>
          </div>

          <div className="flex items-center space-x-4 mb-4">
            <div className="w-12 h-12 bg-purple-500/20 rounded-lg flex items-center justify-center">
              <i className="fas fa-brain text-purple-400 text-lg"></i>
            </div>
            <div className="flex-1">
              <h4 className="font-medium text-slate-200">{gameState.currentLearning.title}</h4>
              <p className="text-sm text-slate-400">{gameState.currentLearning.description}</p>
            </div>
          </div>

          <div className="space-y-2 mb-4">
            <div className="flex justify-between text-sm">
              <span className="text-slate-400">{t('progress')}</span>
              <span className="text-purple-400">
                {gameState.currentLearning.progress}% ({gameState.currentLearning.timeRemaining} {t('remaining')})
              </span>
            </div>
            <Progress value={gameState.currentLearning.progress} className="h-3" />
          </div>

          <div className="p-3 bg-purple-500/10 rounded-lg">
            <p className="text-sm text-purple-300">
              <i className="fas fa-gift mr-1"></i>
              <strong>{t('reward')}:</strong> {getRewardText(gameState.currentLearning.reward, t)}
            </p>
          </div>
        </div>
      )}

      {/* Available Courses */}
      <div className="space-y-6">
        <h3 className="text-lg font-semibold text-slate-200 mb-4">{t('availableCourses')}</h3>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {LEARNING_COURSES.map((course) => {
          const userLevel = gameState.user.level || 1;
          const canAfford = gameState.user.balance >= course.price;
          const hasLevelRequirement = userLevel >= course.requiredLevel;
          const completedLearning = gameState.user.completedLearning || [];
          const isServerLimit25 = course.reward?.type === 'serverSlots' && (gameState.user.serverLimit || 3) >= 25;

          // For server slot courses, only consider them completed if we've reached the 25 server limit
          const isCompleted = course.reward?.type === 'serverSlots' 
            ? isServerLimit25
            : completedLearning.includes(course.id);

          const isLearning = gameState.currentLearning?.id === course.id;
          const hasLearning = gameState.currentLearning && !isLearning;
          const isDisabled = !canAfford || hasLearning || !hasLevelRequirement || isCompleted;



          return (
            <div key={course.id} className={`relative overflow-hidden bg-slate-800/50 backdrop-blur-sm border border-slate-700 rounded-xl p-6 hover:${getLearningColors(course.difficulty).border} transition-all duration-300 transform hover:scale-105 ${
              isLearning ? 'border-purple-500/50 bg-purple-500/5' :
              isCompleted ? 'border-green-500/50 bg-green-500/5' :
              isDisabled ? 'opacity-60' : ''
            }`}>
              {/* Gradient Background */}
              {!isDisabled && (
                <div className={`absolute inset-0 ${!hasLevelRequirement ? 'bg-gradient-to-br from-slate-500/20 to-slate-600/20' : isCompleted ? 'bg-gradient-to-br from-green-500/20 to-green-600/20' : isLearning ? 'bg-gradient-to-br from-purple-500/20 to-purple-600/20' : `bg-gradient-to-br ${getLearningColors(course.difficulty).bg}`} opacity-0 hover:opacity-100 transition-opacity duration-300 rounded-xl`}></div>
              )}
              <div className="flex items-center justify-between mb-4">
                <div className="flex items-center space-x-3">
                  <div className={`relative z-10 w-10 h-10 rounded-full flex items-center justify-center border ${
                    !hasLevelRequirement ? 'bg-slate-500/20 border-slate-500/50' :
                    isCompleted ? 'bg-gradient-to-br from-green-500/20 to-green-600/20 border-green-500/50' : 
                    isLearning ? 'bg-gradient-to-br from-purple-500/20 to-purple-600/20 border-purple-500/50' :
                    `bg-gradient-to-br ${getLearningColors(course.difficulty).bg} ${getLearningColors(course.difficulty).border}`
                  }`}>
                    {!hasLevelRequirement ? (
                      <Lock className="text-slate-400 text-lg" />
                    ) : isCompleted ? (
                      <i className="fas fa-check text-green-400 text-lg"></i>
                    ) : isLearning ? (
                      <i className="fas fa-brain text-purple-400 text-lg"></i>
                    ) : (
                      <i className={`fas fa-brain ${getLearningColors(course.difficulty).icon} text-lg`}></i>
                    )}
                  </div>
                  <div>
                    <h3 className="font-semibold text-slate-100">{course.title}</h3>
                    <p className="text-sm text-slate-400">{course.difficulty}</p>
                    {!hasLevelRequirement && (
                      <p className="text-xs text-red-400 mt-1">{t('requiresLevelX').replace('{level}', course.requiredLevel)}</p>
                    )}
                  </div>
                </div>
                <div className="relative z-10 text-right">
                  <p className="text-base font-bold text-primary">{formatCurrency(course.price)}</p>
                </div>
              </div>

              <p className="relative z-10 text-sm text-slate-400 mb-4">{course.description}</p>

              <div className="relative z-10 grid grid-cols-2 gap-4 mb-4">
                <div className="bg-slate-700/30 rounded-lg p-3">
                  <p className="text-xs text-slate-400 mb-1">{t('duration')}</p>
                  <p className="text-sm font-medium text-slate-300">
                    <i className="fas fa-clock mr-1"></i>
                    {formatTime(course.duration)}
                  </p>
                </div>
                <div className="bg-slate-700/30 rounded-lg p-3">
                  <p className="text-xs text-slate-400 mb-1">{t('reward')}</p>
                  <p className="text-sm font-medium text-secondary">
                    <i className="fas fa-gift mr-1"></i>
                    {getRewardText(course.reward, t)}
                  </p>
                </div>
              </div>

              <Button 
                className="relative z-10 w-full"
                disabled={isDisabled}
                onClick={() => !isCompleted && handleStartCourse(course.id)}
                variant={isCompleted ? "secondary" : isLearning ? "secondary" : isDisabled ? "ghost" : "default"}
              >
                {!hasLevelRequirement ? t('requiresLevelX').replace('{level}', course.requiredLevel) :
                 isServerLimit25 ? t('unavailable') :
                 isCompleted && course.reward?.type !== 'serverSlots' ? t('completed') :
                 isLearning ? t('inProgress') :
                 hasLearning ? t('completeCurrentFirst') :
                 !canAfford ? t('insufficientFunds') :
                 t('startCourse')}
              </Button>

              {!canAfford && !hasLearning && (
                <p className="relative z-10 text-xs text-red-400 mt-2 text-center">
                  {t('needMoreForCourse').replace('{amount}', formatCurrency(course.price - gameState.user.balance))}
                </p>
              )}
            </div>
          );
        })}
        </div>
      </div>

      {/* Benefits Overview */}
      <div className="mt-8 bg-gradient-to-r from-blue-500/10 to-secondary/10 border border-blue-500/30 rounded-xl p-6">
        <h3 className="text-lg font-semibold text-blue-400 mb-3">{t('learningBenefits')}</h3>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-3 sm:gap-4">
          <div className="relative overflow-hidden bg-slate-800/50 backdrop-blur-sm border border-slate-700 rounded-xl p-4 hover:border-blue-500/50 transition-all duration-300 transform hover:scale-105 text-center">
            {/* Blue Gradient Background */}
            <div className="absolute inset-0 bg-gradient-to-br from-blue-500/20 to-blue-600/20 opacity-0 hover:opacity-100 transition-opacity duration-300 rounded-xl"></div>
            <div className="relative z-10 w-12 h-12 bg-gradient-to-br from-blue-500/20 to-blue-600/20 rounded-full flex items-center justify-center mx-auto mb-2 border border-blue-500/50">
              <i className="fas fa-server text-blue-400"></i>
            </div>
            <h4 className="relative z-10 font-medium text-slate-200">{t('moreServers')}</h4>
            <p className="relative z-10 text-xs text-slate-400">{t('unlockAdditionalServerSlots')}</p>
          </div>
          <div className="relative overflow-hidden bg-slate-800/50 backdrop-blur-sm border border-slate-700 rounded-xl p-4 hover:border-orange-500/50 transition-all duration-300 transform hover:scale-105 text-center">
            {/* Orange Gradient Background */}
            <div className="absolute inset-0 bg-gradient-to-br from-orange-500/20 to-orange-600/20 opacity-0 hover:opacity-100 transition-opacity duration-300 rounded-xl"></div>
            <div className="relative z-10 w-12 h-12 bg-gradient-to-br from-orange-500/20 to-orange-600/20 rounded-full flex items-center justify-center mx-auto mb-2 border border-orange-500/50">
              <i className="fas fa-tachometer-alt text-orange-400"></i>
            </div>
            <h4 className="relative z-10 font-medium text-slate-200">{t('higherEfficiency')}</h4>
            <p className="relative z-10 text-xs text-slate-400">{t('increaseServerIncomeRates')}</p>
          </div>
          <div className="relative overflow-hidden bg-slate-800/50 backdrop-blur-sm border border-slate-700 rounded-xl p-4 hover:border-red-500/50 transition-all duration-300 transform hover:scale-105 text-center">
            {/* Red Gradient Background */}
            <div className="absolute inset-0 bg-gradient-to-br from-red-500/20 to-red-600/20 opacity-0 hover:opacity-100 transition-opacity duration-300 rounded-xl"></div>
            <div className="relative z-10 w-12 h-12 bg-gradient-to-br from-red-500/20 to-red-600/20 rounded-full flex items-center justify-center mx-auto mb-2 border border-red-500/50">
              <i className="fas fa-trophy text-red-400"></i>
            </div>
            <h4 className="relative z-10 font-medium text-slate-200">{t('betterRankings')}</h4>
            <p className="relative z-10 text-xs text-slate-400">{t('climbTheLeaderboards')}</p>
          </div>
        </div>
      </div>
    </div>
  );
}

